(function() {

    angular
        .module('meanApp')
        .controller('profileCtrl', profileCtrl);

    profileCtrl.$inject = ['$scope', '$routeParams', 'meanData'];
    function profileCtrl($scope, $routeParams, meanData) {

        var id = $routeParams.id;
        $scope.user = {};

        if(id){
            meanData.getProfileById(id)
                .success(function (data) {
                    $scope.user = data;
                })
                .error(function (e) {
                    console.log(e);
                });
        } else {
            meanData.getProfile()
                .success(function (data) {
                    $scope.user = data;
                })
                .error(function (e) {
                    console.log(e);
                });
        }
    }

})();
(function () {

    angular
        .module('meanApp')
        .controller('navigationCtrl', navigationCtrl);

    navigationCtrl.$inject = ['$scope','authentication'];
    function navigationCtrl($scope, authentication) {

        $scope.isLoggedIn = authentication.isLoggedIn();
        $scope.currentUser = authentication.currentUser();
    }

})();
(function () {

    angular
        .module('meanApp')
        .directive('navigation', navigation);

    function navigation () {
        return {
            restrict: 'EA',
            templateUrl: '/common/directives/navigation/navigation.template.html',
            controller: 'navigationCtrl'
        };
    }

})();
(function () {

    angular
        .module('meanApp')
        .controller('sidebarCtrl', sidebarCtrl);

    sidebarCtrl.$inject = ['$scope','$window','meanData','authentication'];
    function sidebarCtrl($scope, $window, meanData, authentication) {

        $scope.userSidebar = {};

        $scope.isLoggedIn = authentication.isLoggedIn();

        $scope.logOut = function () {
            console.log('logout');
            authentication.logout()
            $window.location.href = '/';
        }
        
        if($scope.isLoggedIn) {
            meanData.getProfile()
                .success(function (data) {
                    $scope.userSidebar = data;
                })
                .error(function (e) {
                    console.log(e);
                });
        }
    }

})();
